function this = ResetConfig( this)        % set parameters used for simulating data to train ANN    this.SimParam.TargetDist = 0.085;     % distance to target = 0.085 m    this.SimParam.TargetDirection = [ 1.00    1.00    1.00                                     -1.00    1.00    1.00                                     -1.00   -1.00    1.00                                      1.00   -1.00    1.00                                      1.00    1.00   -1.00                                     -1.00    1.00   -1.00                                     -1.00   -1.00   -1.00                                      1.00   -1.00   -1.00];    this.SimParam.MovementTime = 0.6;       % movement time      = 0.600 s    this.SimParam.Framerate    = 50;    this.SimParam.SpeedProfileSigma = 0.12;    this.SimParam.NumSimRep    = 5;    this.SimParam.SpeedGains   = [0.9:0.1:1.2];        % init these variables for Predict    this.Members.Gain = 1.0;    this.Members.PredictiveCellsMask = [];    this.Members.NumControlDim = 3;     %ToDo: load NumControlDim from config file    this.Members.NumHiddenUnits = 12;   %ToDo: load NumHiddenUnits from config file    %this.net           = fitnet(this.num_hidden_units, 'trainlm');